INSERT INTO `tb_sejarah` (`id`, `nama_sejarah`, `foto_sejarah`, `tanggal_lahir`, `asal`, `jenis_kelamin`, `deskripsi`) VALUES
(1, ' Sultan Hasanuddin', 'https://idenesia.id/wp-content/uploads/2023/09/sultan_hasanuddin_by_danangkuki_d78gtxe-fullview-1-768x912.jpeg', '1631-01-12 20:36:17', 'Gowa, Sulawesi Selatan', 'Laki-laki', 'Sultan Hasanuddin adalah seorang pahlawan nasional dari Sulawesi Selatan yang berperang melawan VOC Belanda. Dia menjadi simbol perlawanan rakyat Bugis.'),
(3, 'Prof. Dr. Taufik Abdullah', 'https://aanavis.com/wp-content/uploads/2021/11/taufikabdullahjpg_AKT87.jpg', '1936-07-16 08:14:07', 'Padang, Sumatera Barat', 'Laki-laki', ' Profesor Taufik Abdullah adalah figur yang sangat dihormati dalam bidang sejarah sosial ekonomi Indonesia. Dengan keahliannya dalam menganalisis dan menginterpretasi data historis, ia telah memberikan kontribusi besar dalam memahami dinamika sosial ekonomi masyarakat Indonesia dari masa ke masa. Karyanya yang cermat dan mendalam telah membuka wawasan baru bagi banyak generasi sejarawan dan peneliti.'),
(4, 'Teuku Umar', 'https://cdn.antaranews.com/cache/1200x800/2014/01/20140130teuku-umar_medium.jpg', '1854-11-25 08:14:07', 'Aceh', 'Laki-laki', 'Tokoh pejuang asal Aceh yang berperang melawan penjajah Belanda. Terkenal karena kepemimpinan militernya dan strategi perang gerilya yang efektif. '),
(5, 'Tuanku Imam bonjol', 'https://majalahcipeundeuy1.smkn.web.id/wp-content/uploads/sites/4/2022/12/Tuanku-Imam-Bonjol.jpg', '1941-10-30 08:19:34', 'Sumatera Barat', 'Laki-laki', 'Tuanku Imam Bonjol memiliki peran penting dalam memimpin perlawanan terhadap penjajah Belanda di wilayah Minangkabau. Beliau digambarkan sebagai sosok yang sangat berani dan keras kepala dalam mempertahankan tanah air dan agama Islam'),
(6, 'Prof. Dr. Azyumardi Azra', 'https://images.hukumonline.com/frontend/lt6326dbcfd6f18/lt632742cac4602.jpg', '1955-07-04 08:19:34', 'Banjarmasin, Kalimantan Selatan', 'Laki-laki', 'Profesor Azyumardi Azra adalah sejarawan Islam terkemuka Indonesia. Dengan keahliannya dalam memahami dan menganalisis sejarah Islam di Indonesia, ia telah memberikan kontribusi yang besar dalam memperkaya literatur sejarah Islam di Indonesia. Karya-karyanya yang mendalam dan kritis telah menginspirasi banyak generasi akademisi dan intelektual Muslim Indonesia.'),
(7, 'Prof. Dr. Iwan Santosa', 'https://klinikdrindrajana.com/wp-content/uploads/2023/08/22-1.jpg', '1958-02-12 05:22:37', 'Bandung, Jawa Barat', 'Laki-laki', 'Prof. Dr. Iwan Santosa adalah seorang sejarawan ternama yang telah banyak memberikan kontribusi dalam memahami sejarah politik dan sosial Indonesia. Lahir di Bandung pada tahun 1958, Iwan Santosa telah melakukan penelitian mendalam mengenai berbagai aspek sejarah Indonesia, terutama dalam konteks politik dan sosial. Karya-karyanya yang terkenal telah membantu memperkaya pemahaman kita tentang masa lalu negara ini.'),
(8, 'Prof. Dr. Mochtar Buchori', 'https://tokoh.id/wp-content/uploads/2017/07/mochtar-buchori-bio-235x300.jpg', '1962-04-15 05:22:37', 'Surabaya, Jawa Timur', 'Laki-laki', 'Prof. Dr. Mochtar Buchori adalah seorang sejarawan yang sangat dihormati dalam bidang sejarah perkembangan politik Indonesia. Dilahirkan di Surabaya pada tahun 1962, Mochtar Buchori telah mengabdikan hidupnya untuk memahami dan mengajarkan sejarah politik Indonesia kepada generasi muda. Karya-karyanya yang mendalam dan analitis telah menjadi acuan bagi banyak peneliti dan akademisi di bidang ini.'),
(9, 'R.A. Kartini', 'https://asset.kompas.com/crops/Ucm_a4rIh0TcZ0DifRVEdEXfqHM=/106x0:691x390/1200x800/data/photo/2016/04/21/0541552COLLECTIE-TROPENMUSEUM-Gesigneerd-portret-van-Raden-Ajeng-Kartini-TMnr-10018775780x390.jpg', '1879-04-21 11:02:41', 'Jepara, Jawa Tengah\r\n', 'Perempuan', ' R.A. Kartini dikenal sebagai pahlawan emansipasi wanita Indonesia. Dia lahir dalam keluarga Jawa terpandang. Kartini memperjuangkan hak-hak perempuan, khususnya dalam pendidikan.'),
(10, 'Ki Hajar Dewantara', 'https://lh4.googleusercontent.com/proxy/2D_9PoM6lvxyfkciWnIL0AANG3N2f2nQi8H7G-AGHooVO5T36SO-rBgSX_zpOqygYUhdH5QFgov2wXkGXGts9MBLRlgPfvxirIy2ZHuLeXOzoeS_mdcj ', '1889-05-02 11:02:41', 'Yogyakarta, Jawa Tengah', 'Laki-laki', 'Ki Hajar Dewantara adalah pendiri Taman Siswa dan tokoh pendidikan Indonesia. Ia berjuang untuk penyelenggaraan pendidikan nasional yang merata, terutama bagi anak-anak pribumi.'),
(11, 'Mohammad Hatta', 'https://images.bisnis.com/posts/2022/03/14/1510179/hatta2.jpg', '1902-08-12 11:06:22', 'Bukittinggi, Sumatera Barat', 'Laki-laki', 'Mohammad Hatta adalah proklamator Republik Indonesia yang juga dikenal sebagai Bung Hatta. Dia berperan penting dalam memperjuangkan kemerdekaan Indonesia dan menjadi wakil presiden pertama.'),
(12, 'Soekarno', 'https://awsimages.detik.net.id/community/media/visual/2019/08/17/a03e4ead-d3e5-42d1-9fd5-34d9bbb012f0_43.jpeg?w=600&q=90', '1901-06-06 06:06:22', 'Blitar, Jawa Timur', 'Laki-laki', 'Soekarno adalah proklamator dan presiden pertama Indonesia. Dia dikenal dengan orasi-orasinya yang menginspirasi dan kepemimpinannya dalam perjuangan melawan penjajahan.'),
(13, 'Tan Malaka', 'https://static.promediateknologi.id/crop/0x0:0x0/0x0/webp/photo/p2/01/2024/02/24/tan-malaka-bapak-republik-indonesia-yang-kontroversial-1-1108108342.jpg', '2024-05-02 06:09:34', 'Sumbawa, Nusa Tenggara Barat', 'Laki-laki', 'Tan Malaka adalah seorang pemimpin revolusioner Indonesia. Dia aktif dalam gerakan kemerdekaan Indonesia dan memegang peran penting dalam pembentukan Partai Komunis Indonesia.'),
(14, 'Sutan Sjahrir', 'https://images.squarespace-cdn.com/content/v1/5cecd9c1f824ce00010c16c0/1576124355957-4YOYEEPN1LRN91WD0HEU/SutanSjahrir.jpg', '1909-03-05 06:09:34', 'Solok, Sumatera Barat', 'Laki-laki', 'Sutan Sjahrir adalah seorang intelektual dan politisi Indonesia. Dia menjadi perdana menteri pertama Indonesia dan berperan dalam perundingan dengan Belanda.'),
(15, 'Agus Salim', 'https://gurudikdas.kemdikbud.go.id/storage/old-news/1636432005-agus-salim-ist_EDIT_1-1280x720.jpg', '2024-05-02 06:12:08', 'Kota Gede, Yogyakarta', 'Laki-laki', 'Agus Salim adalah seorang ulama, politisi, dan pejuang kemerdekaan Indonesia. Dia berperan dalam diplomasi internasional untuk mendukung kemerdekaan Indonesia.'),
(16, 'Tjut Nyak Dien', 'https://cdn.medcom.id/dynamic/content/2023/11/10/1629956/EP80qTBK36.jpg?w=1024', '1850-05-02 06:12:08', 'Aceh Besar, Aceh', 'Perempuan', 'Tjut Nyak Dien dikenal sebagai pahlawan nasional Indonesia dari Aceh yang berperang melawan penjajah Belanda. Dia dijuluki \"Cut Nyak Dhien\" oleh pemerintah Hindia Belanda.'),
(17, 'Supriyadi', 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQc_7f7By1zpAgyGH6sz6hpFYc6qteIjwsbshf-oHcLgQ&s', '1921-07-01 06:28:15', 'Magelang, Jawa Tengah', 'Laki-laki', ' Supriyadi adalah seorang pejuang kemerdekaan Indonesia dari Magelang. Dia terkenal karena perannya dalam Serangan Umum 1 Maret 1949 di Yogyakarta.'),
(18, 'Sam Ratulangi', 'https://akcdn.detik.net.id/visual/2018/08/16/1f83d3a1-24ef-4561-b441-340f74586168_169.jpeg?w=650', '1890-11-05 06:28:15', 'Tondano, Sulawesi Utara', 'Laki-laki', 'Sam Ratulangi adalah seorang pahlawan nasional Indonesia dari Minahasa. Dia berjuang untuk kemerdekaan Indonesia dan menjadi salah satu tokoh pendiri Partai Nasional Indonesia.'),
(19, 'Diponegoro', 'https://cdn.idntimes.com/content-images/community/2022/07/pangeran-diponegoro-ee935e9ea79a32351151a63f3d9c8cb7-f2eaf94b40cfbacca0570e33e0aabcac_600x400.jpg', '1848-05-02 06:33:21', 'Yogyakarta, Jawa Tengah', 'Laki-laki', 'Diponegoro adalah pahlawan nasional Indonesia yang memimpin perang Jawa melawan penjajah Belanda. Dia dianggap sebagai simbol perlawanan rakyat Jawa.'),
(20, 'Pattimura', 'https://img.inews.co.id/media/600/files/inews_new/2022/07/02/kapitan_pattimura_maluku.jpg', '2024-05-02 06:33:21', 'Saparua, Maluku', 'Laki-laki', 'Pattimura adalah seorang pahlawan nasional Indonesia dari Maluku. Dia memimpin pemberontakan terhadap penjajah Belanda dalam Perang Pattimura.');

